import React from 'react';
import ProductCard from './ProductCard';

export const sampleArtworks = [
  {
    id: 1,
    title: 'Sunset Serenity',
    artist: 'Elena Martinez',
    imageUrl: 'https://images.unsplash.com/photo-1579783902614-a3fb3927b6a5',
    price: 2500,
    medium: 'Oil on Canvas',
    dimensions: '36" × 48"',
    description: 'A breathtaking landscape capturing the golden hour with vibrant hues of orange and purple, reflecting the tranquil beauty of nature.'
  },
  {
    id: 2,
    title: 'Urban Rhythm',
    artist: 'Marcus Chen',
    imageUrl: 'https://images.unsplash.com/photo-1571115764595-644a1f56a55c',
    price: 1800,
    medium: 'Acrylic on Canvas',
    dimensions: '30" × 40"',
    description: 'An abstract interpretation of city life, featuring bold geometric patterns and a dynamic interplay of light and shadow.'
  },
  {
    id: 3,
    title: 'Mystic Forest',
    artist: 'Sarah Williams',
    imageUrl: 'https://images.unsplash.com/photo-1578301978693-85fa9c0320b9',
    price: 3200,
    medium: 'Mixed Media',
    dimensions: '40" × 60"',
    description: 'A mysterious and enchanting portrayal of a forest scene, combining traditional techniques with contemporary elements.'
  },
  {
    id: 4,
    title: 'Ocean Dreams',
    artist: 'David Thompson',
    imageUrl: 'https://images.unsplash.com/photo-1518998053901-5348d3961a04',
    price: 2800,
    medium: 'Watercolor',
    dimensions: '24" × 36"',
    description: 'A serene seascape that captures the ever-changing moods of the ocean with delicate brushstrokes and subtle color gradients.'
  },
  {
    id: 5,
    title: 'Abstract Harmony',
    artist: 'Isabella Romano',
    imageUrl: 'https://images.unsplash.com/photo-1573521193826-58c7dc2e13e3',
    price: 4200,
    medium: 'Oil on Canvas',
    dimensions: '48" × 48"',
    description: 'A bold and energetic composition exploring the relationship between color, form, and movement.'
  },
  {
    id: 6,
    title: 'Mountain Majesty',
    artist: 'Michael Berg',
    imageUrl: 'https://images.unsplash.com/photo-1506744038136-46273834b3fb',
    price: 3500,
    medium: 'Oil on Canvas',
    dimensions: '36" × 54"',
    description: 'A majestic mountain landscape that captures the raw beauty and grandeur of nature in stunning detail.'
  }
];

const Gallery = () => {
  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
      <h2 className="text-3xl font-serif text-center mb-12">Featured Artworks</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {sampleArtworks.map((painting) => (
          <ProductCard key={painting.id} painting={painting} />
        ))}
      </div>
    </div>
  );
};

export default Gallery;
